import React, { useState, useEffect } from 'react';
import { User, Calendar, Star, Gift, LogOut, Home, Grid } from 'lucide-react';

const DailyImageCollector = () => {
  const [user, setUser] = useState(null);
  const [currentView, setCurrentView] = useState('home');
  const [userCollection, setUserCollection] = useState([]);
  const [todaysClaim, setTodaysClaim] = useState(false);
  const [showNewImage, setShowNewImage] = useState(false);
  const [todaysImage, setTodaysImage] = useState(null);
  const [loading, setLoading] = useState(false);

  // Image database with reliable URLs
  const imageDatabase = [
    { id: 1, name: 'Mystic Forest', rarity: 'Common', url: 'https://picsum.photos/400/300?random=1', description: 'A peaceful forest shrouded in morning mist', category: 'Nature' },
    { id: 2, name: 'Desert Sunset', rarity: 'Rare', url: 'https://picsum.photos/400/300?random=2', description: 'Golden dunes under a crimson sky', category: 'Landscape' },
    { id: 3, name: 'Crystal Lake', rarity: 'Epic', url: 'https://picsum.photos/400/300?random=3', description: 'A serene lake reflecting the mountains', category: 'Nature' },
    { id: 4, name: 'Aurora Borealis', rarity: 'Legendary', url: 'https://picsum.photos/400/300?random=4', description: 'Dancing lights in the northern sky', category: 'Celestial' },
    { id: 5, name: 'Ancient Ruins', rarity: 'Common', url: 'https://picsum.photos/400/300?random=5', description: 'Weathered stones telling ancient stories', category: 'Historical' },
    { id: 6, name: 'Cosmic Nebula', rarity: 'Epic', url: 'https://picsum.photos/400/300?random=6', description: 'Swirling colors in deep space', category: 'Celestial' },
    { id: 7, name: 'Cherry Blossom', rarity: 'Rare', url: 'https://picsum.photos/400/300?random=7', description: 'Pink petals dancing in spring breeze', category: 'Nature' },
    { id: 8, name: 'Dragon\'s Peak', rarity: 'Legendary', url: 'https://picsum.photos/400/300?random=8', description: 'A mountain peak touching the clouds', category: 'Landscape' },
    { id: 9, name: 'Zen Garden', rarity: 'Common', url: 'https://picsum.photos/400/300?random=9', description: 'Perfectly raked sand patterns', category: 'Peaceful' },
    { id: 10, name: 'Thunderstorm', rarity: 'Rare', url: 'https://picsum.photos/400/300?random=10', description: 'Lightning illuminating dark clouds', category: 'Weather' },
    { id: 11, name: 'Crystal Cave', rarity: 'Epic', url: 'https://picsum.photos/400/300?random=11', description: 'Glimmering crystals in underground chambers', category: 'Geological' },
    { id: 12, name: 'Phoenix Fire', rarity: 'Legendary', url: 'https://picsum.photos/400/300?random=12', description: 'Mythical flames that never die', category: 'Mythical' },
    { id: 13, name: 'Ocean Depths', rarity: 'Epic', url: 'https://picsum.photos/400/300?random=13', description: 'Mysterious creatures in the deep blue', category: 'Aquatic' },
    { id: 14, name: 'Golden Meadow', rarity: 'Common', url: 'https://picsum.photos/400/300?random=14', description: 'Wildflowers dancing in summer breeze', category: 'Nature' },
    { id: 15, name: 'Ice Palace', rarity: 'Legendary', url: 'https://picsum.photos/400/300?random=15', description: 'A magnificent castle made of ice', category: 'Fantasy' }
  ];

  // Load user data on component mount
  useEffect(() => {
    if (user) {
      loadUserData();
    }
  }, [user]);

  // Check if user can claim today's image
  useEffect(() => {
    if (user) {
      const today = new Date().toDateString();
      const lastClaim = localStorage.getItem(`lastClaim_${user.username}`);
      setTodaysClaim(lastClaim === today);
    }
  }, [user]);

  const loadUserData = () => {
    if (user) {
      const collection = JSON.parse(localStorage.getItem(`collection_${user.username}`) || '[]');
      setUserCollection(collection);
    }
  };

  const getRarityColor = (rarity) => {
    switch (rarity) {
      case 'Common': return 'text-gray-600 bg-gray-100 border-gray-300';
      case 'Rare': return 'text-blue-600 bg-blue-100 border-blue-300';
      case 'Epic': return 'text-purple-600 bg-purple-100 border-purple-300';
      case 'Legendary': return 'text-yellow-700 bg-yellow-100 border-yellow-300';
      default: return 'text-gray-600 bg-gray-100 border-gray-300';
    }
  };

  const getStreakCount = () => {
    if (!user) return 0;
    return parseInt(localStorage.getItem(`streak_${user.username}`) || '0');
  };

  const updateStreak = () => {
    if (!user) return;
    const today = new Date().toDateString();
    const lastClaim = localStorage.getItem(`lastClaim_${user.username}`);
    const yesterday = new Date(Date.now() - 86400000).toDateString();

    let newStreak = 1;
    if (lastClaim === yesterday) {
      const currentStreak = getStreakCount();
      newStreak = currentStreak + 1;
    }

    localStorage.setItem(`streak_${user.username}`, newStreak.toString());
  };

  const login = (username, email) => {
    const userData = { 
      username, 
      email,
      joinDate: new Date().toISOString()
    };
    setUser(userData);
    setCurrentView('home');
  };

  const logout = () => {
    setUser(null);
    setCurrentView('home');
    setUserCollection([]);
    setTodaysClaim(false);
  };

  const drawDailyImage = () => {
    if (todaysClaim || loading) return;

    setLoading(true);

    // Simulate API call delay
    setTimeout(() => {
      // Weighted random selection based on rarity
      const weights = { Common: 50, Rare: 30, Epic: 15, Legendary: 5 };
      const totalWeight = Object.values(weights).reduce((a, b) => a + b, 0);
      const random = Math.random() * totalWeight;

      let currentWeight = 0;
      let selectedRarity = 'Common';

      for (const [rarity, weight] of Object.entries(weights)) {
        currentWeight += weight;
        if (random <= currentWeight) {
          selectedRarity = rarity;
          break;
        }
      }

      const availableImages = imageDatabase.filter(img => img.rarity === selectedRarity);
      const selectedImage = availableImages[Math.floor(Math.random() * availableImages.length)];

      // Add to collection
      const newCollection = [...userCollection, { 
        ...selectedImage, 
        dateObtained: new Date().toISOString(),
        id: `${selectedImage.id}_${Date.now()}` // Ensure unique ID
      }];
      setUserCollection(newCollection);
      localStorage.setItem(`collection_${user.username}`, JSON.stringify(newCollection));

      // Update streak and mark as claimed today
      updateStreak();
      const today = new Date().toDateString();
      localStorage.setItem(`lastClaim_${user.username}`, today);
      setTodaysClaim(true);
      setTodaysImage(selectedImage);
      setShowNewImage(true);
      setLoading(false);
    }, 1500); // 1.5 second delay for effect
  };

  const LoginScreen = () => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [isLogin, setIsLogin] = useState(true);

    const handleSubmit = (e) => {
      e.preventDefault();
      if (username.trim()) {
        login(username, email);
      }
    };

    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-600 to-blue-600 flex items-center justify-center p-4">
        <div className="bg-white rounded-lg shadow-xl p-8 max-w-md w-full">
          <div className="text-center mb-8">
            <Gift className="mx-auto h-16 w-16 text-purple-600 mb-4" />
            <h1 className="text-3xl font-bold text-gray-800">Daily Image Collector</h1>
            <p className="text-gray-600 mt-2">Discover a new image every day!</p>
            <div className="flex items-center justify-center space-x-2 mt-2">
              <div className="w-2 h-2 bg-green-500 rounded-full"></div>
              <p className="text-xs text-green-600">Ready to Deploy!</p>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Username
              </label>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                placeholder="Enter your username"
                required
              />
            </div>

            {!isLogin && (
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Email
                </label>
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                  placeholder="Enter your email"
                />
              </div>
            )}

            <button
              type="submit"
              className="w-full bg-purple-600 hover:bg-purple-700 text-white font-medium py-3 px-4 rounded-lg transition-colors"
            >
              {isLogin ? 'Login' : 'Sign Up'}
            </button>
          </form>

          <div className="mt-4 text-center">
            <button
              onClick={() => setIsLogin(!isLogin)}
              className="text-purple-600 hover:text-purple-700 text-sm"
            >
              {isLogin ? "Don't have an account? Sign up" : "Already have an account? Login"}
            </button>
          </div>

          <div className="mt-6 space-y-2">
            <button
              onClick={() => login('demo_user', 'demo@example.com')}
              className="w-full bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-4 rounded-lg transition-colors text-sm"
            >
              Quick Demo Login
            </button>
          </div>
        </div>
      </div>
    );
  };

  const HomeScreen = () => (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 to-blue-600 p-4">
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow-xl p-6 mb-6">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center space-x-3">
              <User className="h-8 w-8 text-purple-600" />
              <div>
                <h2 className="text-xl font-bold text-gray-800">Welcome, {user.username}!</h2>
                <p className="text-gray-600">Collection: {userCollection.length} images</p>
                <p className="text-gray-600">Daily Streak: {getStreakCount()} days</p>
                <div className="flex items-center space-x-2 mt-1">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <p className="text-xs text-green-600">Production Ready</p>
                </div>
              </div>
            </div>
            <button
              onClick={logout}
              className="flex items-center space-x-2 text-gray-600 hover:text-gray-800 transition-colors"
            >
              <LogOut className="h-5 w-5" />
              <span>Logout</span>
            </button>
          </div>

          <div className="grid md:grid-cols-2 gap-6">
            <div className="bg-gradient-to-r from-purple-100 to-blue-100 rounded-lg p-6">
              <div className="flex items-center space-x-3 mb-4">
                <Gift className="h-8 w-8 text-purple-600" />
                <h3 className="text-xl font-bold text-gray-800">Daily Draw</h3>
              </div>

              {!todaysClaim ? (
                <div>
                  <p className="text-gray-600 mb-4">Ready to discover today's image?</p>
                  <button
                    onClick={drawDailyImage}
                    disabled={loading}
                    className="w-full bg-purple-600 hover:bg-purple-700 disabled:bg-purple-400 text-white font-medium py-3 px-4 rounded-lg transition-colors flex items-center justify-center"
                  >
                    {loading ? (
                      <>
                        <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                        Drawing...
                      </>
                    ) : (
                      'Draw Today\'s Image'
                    )}
                  </button>
                </div>
              ) : (
                <div>
                  <p className="text-gray-600 mb-4">You've already claimed today's image!</p>
                  <div className="flex items-center space-x-2 text-green-600">
                    <Calendar className="h-5 w-5" />
                    <span>Come back tomorrow for a new image</span>
                  </div>
                </div>
              )}
            </div>

            <div className="bg-gradient-to-r from-blue-100 to-purple-100 rounded-lg p-6">
              <div className="flex items-center space-x-3 mb-4">
                <Grid className="h-8 w-8 text-blue-600" />
                <h3 className="text-xl font-bold text-gray-800">Your Collection</h3>
              </div>

              <div className="space-y-2">
                <div className="flex justify-between">
                  <span className="text-gray-600">Total Images:</span>
                  <span className="font-bold">{userCollection.length}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Daily Streak:</span>
                  <span className="font-bold">{getStreakCount()}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Rarest:</span>
                  <span className="font-bold">
                    {userCollection.length > 0 
                      ? userCollection.reduce((prev, curr) => {
                          const rarityOrder = { Common: 1, Rare: 2, Epic: 3, Legendary: 4 };
                          return rarityOrder[curr.rarity] > rarityOrder[prev.rarity] ? curr : prev;
                        }).rarity
                      : 'None'
                    }
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Completion:</span>
                  <span className="font-bold">{Math.round((userCollection.length / imageDatabase.length) * 100)}%</span>
                </div>
              </div>

              <button
                onClick={() => setCurrentView('collection')}
                className="w-full mt-4 bg-blue-600 hover:bg-blue-700 text-white font-medium py-3 px-4 rounded-lg transition-colors"
              >
                View Collection
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const CollectionScreen = () => (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 to-blue-600 p-4">
      <div className="max-w-6xl mx-auto">
        <div className="bg-white rounded-lg shadow-xl p-6">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-2xl font-bold text-gray-800">Your Collection ({userCollection.length}/{imageDatabase.length})</h2>
            <button
              onClick={() => setCurrentView('home')}
              className="flex items-center space-x-2 text-gray-600 hover:text-gray-800 transition-colors"
            >
              <Home className="h-5 w-5" />
              <span>Back to Home</span>
            </button>
          </div>

          {userCollection.length === 0 ? (
            <div className="text-center py-12">
              <Gift className="mx-auto h-16 w-16 text-gray-400 mb-4" />
              <h3 className="text-xl font-bold text-gray-600 mb-2">No images collected yet</h3>
              <p className="text-gray-500">Draw your first daily image to start building your collection!</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {userCollection.map((image, index) => (
                <div key={index} className="bg-gray-50 rounded-lg overflow-hidden shadow-md hover:shadow-lg transition-shadow">
                  <img
                    src={image.url || image.imageUrl}
                    alt={image.name || image.imageName}
                    className="w-full h-48 object-cover"
                    onError={(e) => {
                      console.log('Image failed to load:', image);
                      e.target.src = 'https://via.placeholder.com/400x300?text=Image+Not+Found';
                    }}
                  />
                  <div className="p-4">
                    <div className="flex items-center justify-between mb-2">
                      <h3 className="font-bold text-gray-800">{image.name || image.imageName}</h3>
                      <span className={`px-2 py-1 rounded-full text-xs font-medium border ${getRarityColor(image.rarity)}`}>
                        {image.rarity}
                      </span>
                    </div>
                    <p className="text-gray-600 text-sm mb-2">{image.description}</p>
                    <div className="flex justify-between items-center">
                      <span className="text-xs text-purple-600 font-medium">{image.category}</span>
                      <span className="text-xs text-gray-500">
                        {new Date(image.dateObtained).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );

  // New image modal
  const NewImageModal = () => (
    showNewImage && todaysImage && (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
        <div className="bg-white rounded-lg shadow-xl max-w-md w-full p-6">
          <div className="text-center">
            <Star className="mx-auto h-12 w-12 text-yellow-500 mb-4" />
            <h3 className="text-xl font-bold text-gray-800 mb-2">New Image Discovered!</h3>
            <img
              src={todaysImage.url}
              alt={todaysImage.name}
              className="w-full h-48 object-cover rounded-lg mb-4"
            />
            <h4 className="font-bold text-lg text-gray-800">{todaysImage.name}</h4>
            <span className={`inline-block px-3 py-1 rounded-full text-sm font-medium border ${getRarityColor(todaysImage.rarity)} mb-2`}>
              {todaysImage.rarity}
            </span>
            <p className="text-gray-600 text-sm mb-4">{todaysImage.description}</p>
            <button
              onClick={() => setShowNewImage(false)}
              className="w-full bg-purple-600 hover:bg-purple-700 text-white font-medium py-2 px-4 rounded-lg transition-colors"
            >
              Add to Collection
            </button>
          </div>
        </div>
      </div>
    )
  );

  if (!user) {
    return <LoginScreen />;
  }

  return (
    <div>
      {currentView === 'home' && <HomeScreen />}
      {currentView === 'collection' && <CollectionScreen />}
      <NewImageModal />
    </div>
  );
};

export default DailyImageCollector;
