// src/App.js

import React from 'react';
import { useGameLogic } from './hooks/useGameLogic';
import LoginScreen from './components/LoginScreen';
import HomeScreen from './components/HomeScreen';
import CollectionScreen from './components/CollectionScreen';
import NewImageModal from './components/NewImageModal';
import ImageDetailModal from './components/ImageDetailModal';
import './styles/animations.css'; // Import the animations

const DailyImageCollector = () => {
  const {
    user,
    login,
    logout,
    currentView,
    setCurrentView,
    userCollection,
    todaysClaim,
    drawDailyImage,
    loading,
    getStreakCount,
    imageDatabase,
    showNewImage,
    setShowNewImage,
    todaysImage,
    shakeAnimation,
    setShakeAnimation,
    selectedImage,
    setSelectedImage,
    collectionView,
    setCollectionView,
    isFullscreen,
    setIsFullscreen
  } = useGameLogic();

  if (!user) {
    return <LoginScreen login={login} />;
  }

  return (
    <div>
      {currentView === 'home' && (
        <HomeScreen
          user={user}
          logout={logout}
          drawDailyImage={drawDailyImage}
          loading={loading}
          todaysClaim={todaysClaim}
          userCollection={userCollection}
          getStreakCount={getStreakCount}
          imageDatabase={imageDatabase}
          setCurrentView={setCurrentView}
        />
      )}
      {currentView === 'collection' && (
        <CollectionScreen
          userCollection={userCollection}
          imageDatabase={imageDatabase}
          setCurrentView={setCurrentView}
          setSelectedImage={setSelectedImage}
          collectionView={collectionView}
          setCollectionView={setCollectionView}
        />
      )}
      <NewImageModal
        showNewImage={showNewImage}
        setShowNewImage={setShowNewImage}
        todaysImage={todaysImage}
        shakeAnimation={shakeAnimation}
        setShakeAnimation={setShakeAnimation}
      />
      <ImageDetailModal
        selectedImage={selectedImage}
        setSelectedImage={setSelectedImage}
        isFullscreen={isFullscreen}
        setIsFullscreen={setIsFullscreen}
      />
    </div>
  );
};

export default DailyImageCollector;
