import { useState, useEffect, useCallback } from 'react';

export const useGameLogic = () => {
  // User and authentication state
  const [user, setUser] = useState(null);
  const [currentView, setCurrentView] = useState('home');
  
  // Collection and image state
  const [userCollection, setUserCollection] = useState([]);
  const [imageDatabase, setImageDatabase] = useState([]);
  const [todaysImage, setTodaysImage] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  
  // UI state
  const [showNewImage, setShowNewImage] = useState(false);
  const [shakeAnimation, setShakeAnimation] = useState(false);
  const [loading, setLoading] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  
  // Daily draws state - NEW
  const [dailyDrawsRemaining, setDailyDrawsRemaining] = useState(3);
  const [totalDrawsToday, setTotalDrawsToday] = useState(0);
  const [drawsResetTime, setDrawsResetTime] = useState(null);
  const [lastDrawDate, setLastDrawDate] = useState(null);

  // Legacy state for compatibility
  const [todaysClaim, setTodaysClaim] = useState(false);

  // Maximum draws per day
  const MAX_DRAWS_PER_DAY = 3;

  // Sample image database - replace with your actual data
  useEffect(() => {
    // Load your image database here
    setImageDatabase([
      // Your image database from the JSON you provided earlier
    ]);
  }, []);

  // Initialize daily draws system
  useEffect(() => {
    if (user) {
      initializeDailyDraws();
    }
  }, [user]);

  const initializeDailyDraws = () => {
    const today = new Date().toDateString();
    const savedData = localStorage.getItem(`dailyDraws_${user.username}`);
    
    if (savedData) {
      const { date, drawsUsed } = JSON.parse(savedData);
      
      if (date === today) {
        // Same day - restore progress
        setTotalDrawsToday(drawsUsed);
        setDailyDrawsRemaining(MAX_DRAWS_PER_DAY - drawsUsed);
        setTodaysClaim(drawsUsed > 0); // Legacy compatibility
      } else {
        // New day - reset draws
        resetDailyDraws();
      }
    } else {
      // First time - initialize
      resetDailyDraws();
    }
    
    // Set reset time to next midnight
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    tomorrow.setHours(0, 0, 0, 0);
    setDrawsResetTime(tomorrow);
  };

  const resetDailyDraws = () => {
    setTotalDrawsToday(0);
    setDailyDrawsRemaining(MAX_DRAWS_PER_DAY);
    setTodaysClaim(false); // Legacy compatibility
    
    // Save to localStorage
    const today = new Date().toDateString();
    localStorage.setItem(`dailyDraws_${user.username}`, JSON.stringify({
      date: today,
      drawsUsed: 0
    }));
  };

  const saveDailyDrawProgress = (drawsUsed) => {
    const today = new Date().toDateString();
    localStorage.setItem(`dailyDraws_${user.username}`, JSON.stringify({
      date: today,
      drawsUsed: drawsUsed
    }));
  };

  // Image drawing logic
  const performImageDraw = async () => {
    // Simulate API call or random selection from imageDatabase
    const randomIndex = Math.floor(Math.random() * imageDatabase.length);
    const selectedImage = imageDatabase[randomIndex];
    
    // Simulate network delay
    await new Promise(resolve => setTimeout(resolve, 1000));
    
    return selectedImage;
  };

  // Modified drawDailyImage function
  const drawDailyImage = useCallback(async () => {
    // Check if user has draws remaining (admin override)
    if (dailyDrawsRemaining <= 0 && user.username !== 'admin') {
      console.log('No draws remaining today');
      return;
    }

    setLoading(true);
    
    try {
      // Perform the draw
      const drawnImage = await performImageDraw();
      
      // Update draw counts (skip for admin)
      if (user.username !== 'admin') {
        const newDrawsUsed = totalDrawsToday + 1;
        const newDrawsRemaining = MAX_DRAWS_PER_DAY - newDrawsUsed;
        
        setTotalDrawsToday(newDrawsUsed);
        setDailyDrawsRemaining(newDrawsRemaining);
        setTodaysClaim(true); // Legacy compatibility
        
        // Save progress
        saveDailyDrawProgress(newDrawsUsed);
      }
      
      // Update UI state
      setTodaysImage(drawnImage);
      setShowNewImage(true);
      setShakeAnimation(true);
      
      // Add to collection
      const newCard = {
        ...drawnImage,
        id: `${drawnImage.id}_${Date.now()}`,
        dateObtained: new Date().toISOString()
      };
      
      setUserCollection(prev => [...prev, newCard]);
      
    } catch (error) {
      console.error('Error drawing image:', error);
    } finally {
      setLoading(false);
    }
  }, [dailyDrawsRemaining, totalDrawsToday, user, imageDatabase]);

  // Check for daily reset (call this periodically)
  const checkDailyReset = useCallback(() => {
    if (!user) return;
    
    const now = new Date();
    const today = now.toDateString();
    const savedData = localStorage.getItem(`dailyDraws_${user.username}`);
    
    if (savedData) {
      const { date } = JSON.parse(savedData);
      
      // If date changed, reset draws
