    if (user && user.username === 'admin') {
      return true; // Admin has unlimited draws
    }
    return dailyDrawsRemaining > 0;
  };

  const getDrawButtonText = () => {
    if (user && user.username === 'admin') {
      return 'ðŸŽ¯ Draw Card (Unlimited)';
    }
    
    if (dailyDrawsRemaining === 0) {
      return 'â° No Draws Left Today';
    }
    
    return `ðŸŽ¯ Draw Card (${dailyDrawsRemaining} left)`;
  };

  const getTimeUntilReset = () => {
    if (!drawsResetTime) return null;
    
    const now = new Date();
    const diff = drawsResetTime - now;
    
    if (diff <= 0) return null;
    
    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
    
    return { hours, minutes };
  };

  // User management
  const login = (username, email) => {
    const newUser = { username, email };
    setUser(newUser);
    
    // Load user's collection from localStorage
    const savedCollection = localStorage.getItem(`collection_${username}`);
    if (savedCollection) {
      setUserCollection(JSON.parse(savedCollection));
    }
  };

  const logout = () => {
    setUser(null);
    setUserCollection([]);
    setCurrentView('home');
    setTodaysImage(null);
    setSelectedImage(null);
    setShowNewImage(false);
    setDailyDrawsRemaining(3);
    setTotalDrawsToday(0);
    setTodaysClaim(false);
  };

  // Save collection when it changes
  useEffect(() => {
    if (user && userCollection.length > 0) {
      localStorage.setItem(`collection_${user.username}`, JSON.stringify(userCollection));
    }
  }, [user, userCollection]);

  // Streak count calculation
  const getStreakCount = () => {
    // Simple implementation - you can make this more sophisticated
    const today = new Date().toDateString();
    const savedStreak = localStorage.getItem(`streak_${user?.username}`);
    
    if (savedStreak) {
      const { date, count } = JSON.parse(savedStreak);
      if (date === today) {
        return count;
      }
    }
    
    return totalDrawsToday;
  };

  return {
    // User state
    user,
    login,
    logout,
    
    // View state
    currentView,
    setCurrentView,
    
    // Collection state
    userCollection,
    imageDatabase,
    todaysImage,
    selectedImage,
    setSelectedImage,
    
    // UI state
    showNewImage,
    setShowNewImage,
    shakeAnimation,
    setShakeAnimation,
    loading,
    isFullscreen,
    setIsFullscreen,
    
    // Draw functionality
    drawDailyImage,
    todaysClaim, // Legacy compatibility
    getStreakCount,
    
    // New 3-draws-per-day functionality
    dailyDrawsRemaining,
    totalDrawsToday,
    drawsResetTime,
    canDraw,
    getDrawButtonText,
    getTimeUntilReset,
    MAX_DRAWS_PER_DAY
  };
};
