// src/components/CollectionScreen.js

import React, { useMemo, useState } from 'react';
import { Home, Star, BookOpen, Grid3X3 } from 'lucide-react';
import { getRarityColor } from '../utils/helpers';

const CollectionScreen = ({
  userCollection,
  imageDatabase,
  setCurrentView,
  setSelectedImage,
}) => {
  const [currentSubView, setCurrentSubView] = useState('pokedex'); // 'pokedex' or 'cardset'

  // WoW-style rarity border effects
  const getRarityBorderEffect = (rarity) => {
    const effects = {
      Common: {
        border: 'border-2 border-gray-400',
        glow: 'shadow-md',
        animation: ''
      },
      Rare: {
        border: 'border-2 border-blue-500',
        glow: 'shadow-lg shadow-blue-500/30',
        animation: 'hover:shadow-blue-500/50'
      },
      Epic: {
        border: 'border-3 border-purple-600',
        glow: 'shadow-xl shadow-purple-600/40',
        animation: 'hover:shadow-purple-600/60 animate-pulse'
      },
      Legendary: {
        border: 'border-4 border-orange-500',
        glow: 'shadow-2xl shadow-orange-500/50',
        animation: 'hover:shadow-orange-500/70 animate-pulse'
      }
    };
    return effects[rarity] || effects.Common;
  };

  const pokedexEntries = useMemo(() => {
    return imageDatabase.map((templateImage) => {
      const collectedImage = userCollection.find(item => {
        const itemId = item.id || item.imageID;
        const templateId = templateImage.id;
        return String(itemId).split('_')[0] === String(templateId) ||
               String(itemId) === String(templateId) ||
               item.name === templateImage.name ||
               item.imageName === templateImage.name;
      });
      return {
        ...templateImage,
        isCollected: !!collectedImage,
        dateObtained: collectedImage?.dateObtained || null,
        collectedData: collectedImage,
      };
    });
  }, [userCollection, imageDatabase]);

  const collectedCount = useMemo(() => pokedexEntries.filter(entry => entry.isCollected).length, [pokedexEntries]);

  const PokedexView = () => {
    // Group entries by rarity
    const rarityOrder = ['Common', 'Rare', 'Epic', 'Legendary'];
    const groupedByRarity = rarityOrder.reduce((groups, rarity) => {
      groups[rarity] = pokedexEntries.filter(entry => entry.rarity === rarity);
      return groups;
    }, {});

    // Rarity section styling
    const getRaritySectionStyle = (rarity) => {
      const styles = {
        Common: {
          bg: 'bg-gradient-to-br from-gray-50 to-gray-100',
          border: 'border-gray-300',
          title: 'text-gray-700',
          emoji: 'âšª'
        },
        Rare: {
          bg: 'bg-gradient-to-br from-blue-50 to-blue-100',
          border: 'border-blue-300',
          title: 'text-blue-700',
          emoji: 'ðŸ”µ'
        },
        Epic: {
          bg: 'bg-gradient-to-br from-purple-50 to-purple-100',
          border: 'border-purple-300',
          title: 'text-purple-700',
          emoji: 'ðŸŸ£'
        },
        Legendary: {
          bg: 'bg-gradient-to-br from-orange-50 to-orange-100',
          border: 'border-orange-300',
          title: 'text-orange-700',
          emoji: 'ðŸŸ '
        }
      };
      return styles[rarity] || styles.Common;
    };

    const renderCard = (entry, globalIndex) => {
      const rarityEffect = entry.isCollected ? getRarityBorderEffect(entry.rarity) : { border: 'border border-gray-300', glow: 'shadow-sm', animation: '' };
      return (
        <div
          key={entry.id}
          className={`rounded-lg overflow-hidden transition-all ${
            entry.isCollected 
              ? `bg-white cursor-pointer transform hover:scale-105 ${rarityEffect.border} ${rarityEffect.glow} ${rarityEffect.animation}` 
              : 'bg-gray-100 opacity-75 border border-gray-300'
          }`}
          onClick={() => entry.isCollected && setSelectedImage(entry.collectedData)}
        >
          <div className="relative">
            {entry.isCollected ? (
              <img src={entry.url} alt={entry.name} className="w-full h-32 object-cover" onError={(e) => { e.target.src = 'https://via.placeholder.com/400x300?text=Image+Not+Found'; }} />
            ) : (
              <div className="w-full h-32 bg-gradient-to-br from-gray-200 to-gray-300 flex items-center justify-center">
                <div className="text-center"><div className="text-4xl text-gray-400 mb-1">?</div><div className="text-xs text-gray-500">Not Found</div></div>
              </div>
            )}
            <div className="absolute top-2 left-2 bg-black bg-opacity-50 text-white text-xs px-2 py-1 rounded">#{String(globalIndex + 1).padStart(3, '0')}</div>
            {entry.isCollected && <div className="absolute top-2 right-2"><span className={`px-2 py-1 rounded-full text-xs font-medium ${getRarityColor(entry.rarity)}`}>{entry.rarity}</span></div>}
            {entry.isCollected && <div className="absolute bottom-2 right-2 bg-green-500 text-white rounded-full p-1"><svg className="h-3 w-3" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" /></svg></div>}
            {/* Epic/Legendary rarity indicator */}
            {entry.isCollected && (entry.rarity === 'Epic' || entry.rarity === 'Legendary') && (
              <div className="absolute top-2 left-8">
                <div className={`w-2 h-2 rounded-full ${entry.rarity === 'Epic' ? 'bg-purple-600' : 'bg-orange-500'} animate-pulse`}></div>
              </div>
            )}
          </div>
          <div className="p-3">
            <h3 className={`font-bold text-sm ${entry.isCollected ? 'text-gray-800' : 'text-gray-400'}`}>{entry.isCollected ? entry.name : '???'}</h3>
            <p className={`text-xs mt-1 ${entry.isCollected ? 'text-gray-600' : 'text-gray-400'}`}>{entry.isCollected ? entry.category : 'Unknown'}</p>
            {entry.isCollected && entry.dateObtained && <p className="text-xs text-gray-500 mt-1">{new Date(entry.dateObtained).toLocaleDateString()}</p>}
          </div>
        </div>
      );
    };

    return (
      <>
        <div className="space-y-8">
          {rarityOrder.map(rarity => {
            const entries = groupedByRarity[rarity];
            if (entries.length === 0) return null;

            const sectionStyle = getRaritySectionStyle(rarity);
            const collectedInRarity = entries.filter(entry => entry.isCollected).length;
            const globalStartIndex = rarityOrder.slice(0, rarityOrder.indexOf(rarity))
              .reduce((acc, prevRarity) => acc + groupedByRarity[prevRarity].length, 0);

            return (
              <div key={rarity} className={`${sectionStyle.bg} ${sectionStyle.border} border-2 rounded-xl p-4 sm:p-6`}>
                {/* Rarity Header */}
                <div className="flex items-center justify-between mb-4">
                  <div className="flex items-center space-x-3">
                    <span className="text-2xl">{sectionStyle.emoji}</span>
                    <h3 className={`text-xl sm:text-2xl font-bold ${sectionStyle.title}`}>
                      {rarity}
                    </h3>
                  </div>
                  <div className={`${sectionStyle.title} font-semibold text-sm sm:text-base`}>
                    {collectedInRarity}/{entries.length} Collected
                  </div>
                </div>

                {/* Progress Bar */}
                <div className="mb-6">
                  <div className="bg-white bg-opacity-50 rounded-full h-2 overflow-hidden">
                    <div 
                      className={`h-full transition-all duration-500 ${
                        rarity === 'Common' ? 'bg-gray-500' :
                        rarity === 'Rare' ? 'bg-blue-500' :
                        rarity === 'Epic' ? 'bg-purple-500' :
                        'bg-orange-500'
                      }`}
                      style={{ width: `${(collectedInRarity / entries.length) * 100}%` }}
                    />
                  </div>
                </div>

                {/* Cards Grid */}
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4">
                  {entries.map((entry, index) => renderCard(entry, globalStartIndex + index))}
                </div>
              </div>
            );
          })}
        </div>

        {collectedCount === imageDatabase.length && (
          <div className="mt-8 text-center p-6 bg-gradient-to-r from-yellow-100 to-orange-100 rounded-lg">
            <Star className="mx-auto h-12 w-12 text-yellow-500 mb-4" />
            <h3 className="text-xl font-bold text-gray-800 mb-2">ðŸŽ‰ PokÃ©dex Complete! ðŸŽ‰</h3>
            <p className="text-gray-600">Congratulations! You've collected all {imageDatabase.length} images!</p>
          </div>
        )}
      </>
    );
  };

  const CardSetView = () => {
    // Group cards by base ID and count occurrences
    const cardSets = useMemo(() => {
      const sets = {};
      
      userCollection.forEach(card => {
        const baseId = card.id.toString().split('_')[0];
        const templateCard = imageDatabase.find(template => template.id.toString() === baseId);
        
        if (templateCard) {
          if (!sets[baseId]) {
            sets[baseId] = {
              ...templateCard,
              baseId,
              copies: [],
              count: 0
            };
          }
          sets[baseId].copies.push(card);
          sets[baseId].count++;
        }
      });
      
      return Object.values(sets).sort((a, b) => parseInt(a.baseId) - parseInt(b.baseId));
    }, [userCollection, imageDatabase]);

    return (
      <div className="space-y-6">
        <div className="text-center mb-6">
          <h3 className="text-2xl font-bold text-gray-800 mb-2">ðŸŽ´ Card Set Collection</h3>
          <p className="text-gray-600">View all your collected cards with duplicate counts</p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {cardSets.map((cardSet) => {
            const rarityEffect = getRarityBorderEffect(cardSet.rarity);
            
            return (
              <div
                key={cardSet.baseId}
                className={`bg-white rounded-lg overflow-hidden transition-all transform hover:scale-105 cursor-pointer ${rarityEffect.border} ${rarityEffect.glow} ${rarityEffect.animation}`}
                onClick={() => setSelectedImage(cardSet.copies[0])}
              >
                <div className="relative">
                  <img 
                    src={cardSet.url} 
                    alt={cardSet.name} 
                    className="w-full h-48 object-cover"
                    onError={(e) => { 
                      e.target.src = 'https://via.placeholder.com/400x300?text=Image+Not+Found'; 
                    }} 
                  />
                  
                  {/* Card ID */}
                  <div className="absolute top-3 left-3 bg-black bg-opacity-60 text-white text-sm font-bold px-2 py-1 rounded">
                    ID: {cardSet.baseId}
                  </div>
                  
                  {/* Count Badge */}
                  <div className="absolute top-3 right-3 bg-gradient-to-r from-blue-500 to-indigo-500 text-white text-sm font-bold px-3 py-1 rounded-full shadow-lg">
                    x{cardSet.count}
                  </div>
                  
                  {/* Rarity Badge */}
                  <div className="absolute bottom-3 right-3">
                    <span className={`px-3 py-1 rounded-full text-sm font-bold ${getRarityColor(cardSet.rarity)}`}>
                      {cardSet.rarity}
                    </span>
                  </div>

                  {/* Epic/Legendary indicator */}
                  {(cardSet.rarity === 'Epic' || cardSet.rarity === 'Legendary') && (
                    <div className="absolute bottom-3 left-3">
                      <div className={`w-3 h-3 rounded-full ${cardSet.rarity === 'Epic' ? 'bg-purple-600' : 'bg-orange-500'} animate-pulse shadow-lg`}></div>
                    </div>
                  )}
                </div>
                
                <div className="p-4">
                  <h3 className="font-bold text-lg text-gray-800 mb-1">{cardSet.name}</h3>
                  <p className="text-sm text-gray-600 mb-2">{cardSet.category}</p>
                  
                  {/* Collection dates */}
                  <div className="text-xs text-gray-500">
                    <div>First: {new Date(cardSet.copies[0].dateObtained).toLocaleDateString()}</div>
                    {cardSet.count > 1 && (
                      <div>Latest: {new Date(cardSet.copies[cardSet.copies.length - 1].dateObtained).toLocaleDateString()}</div>
                    )}
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        {cardSets.length === 0 && (
          <div className="text-center py-12">
            <div className="text-6xl mb-4">ðŸ“­</div>
            <h3 className="text-xl font-bold text-gray-600 mb-2">No Cards Collected Yet</h3>
            <p className="text-gray-500">Start drawing daily images to build your collection!</p>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 to-blue-600 p-2 sm:p-4">
      <div className="max-w-6xl mx-auto">
        <div className="bg-white rounded-lg shadow-xl overflow-hidden">
          {/* Enhanced Mobile-Friendly Header */}
          <div className="bg-gradient-to-r from-purple-600 to-blue-600 text-white p-4 sm:p-6">
            <div className="flex flex-col space-y-4">
              {/* Title and Progress */}
              <div className="text-center sm:text-left">
                <h2 className="text-xl sm:text-2xl font-bold mb-1">
                  ðŸ“š Collection Machicoulis
                </h2>
                <div className="flex items-center justify-center sm:justify-start space-x-2">
                  <div className="bg-white bg-opacity-20 rounded-full px-3 py-1">
                    <span className="text-sm font-medium">
                      {collectedCount}/{imageDatabase.length} Discovered
                    </span>
                  </div>
                  <div className="bg-white bg-opacity-20 rounded-full px-3 py-1">
                    <span className="text-sm font-medium">
                      {Math.round((collectedCount / imageDatabase.length) * 100)}%
                    </span>
                  </div>
                </div>
              </div>
              
              {/* Home Button - Always Visible */}
              <div className="flex justify-center sm:justify-end">
                <button
                  onClick={() => setCurrentView('home')}
                  className="flex items-center justify-center space-x-2 bg-white bg-opacity-20 hover:bg-opacity-30 px-4 py-2 rounded-lg text-sm font-medium transition-all w-full sm:w-auto"
                >
                  <Home className="h-4 w-4" />
                  <span>Back Home</span>
                </button>
              </div>
            </div>
          </div>

          {/* Submenu */}
          <div className="bg-gray-50 border-b border-gray-200 px-4 sm:px-6">
            <div className="flex justify-center space-x-1">
              <button
                onClick={() => setCurrentSubView('pokedex')}
                className={`flex items-center space-x-2 px-4 py-3 text-sm font-medium transition-all ${
                  currentSubView === 'pokedex'
                    ? 'border-b-2 border-purple-500 text-purple-600 bg-white'
                    : 'text-gray-600 hover:text-gray-800 hover:bg-gray-100'
                } rounded-t-lg`}
              >
                <BookOpen className="h-4 w-4" />
                <span>PokÃ©dex View</span>
              </button>
              <button
                onClick={() => setCurrentSubView('cardset')}
                className={`flex items-center space-x-2 px-4 py-3 text-sm font-medium transition-all ${
                  currentSubView === 'cardset'
                    ? 'border-b-2 border-purple-500 text-purple-600 bg-white'
                    : 'text-gray-600 hover:text-gray-800 hover:bg-gray-100'
                } rounded-t-lg`}
              >
                <Grid3X3 className="h-4 w-4" />
                <span>Card Set View</span>
              </button>
            </div>
          </div>
          
          {/* Content */}
          <div className="p-4 sm:p-6">
            {currentSubView === 'pokedex' ? <PokedexView /> : <CardSetView />}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CollectionScreen;
