// src/components/CollectionScreen.js

import React, { useMemo } from 'react';
import { Home, Gift, Star, BookOpen, Grid3X3 } from 'lucide-react';
import { getRarityColor } from '../utils/helpers';

const CollectionScreen = ({
  userCollection,
  imageDatabase,
  setCurrentView,
  setSelectedImage,
  collectionView,
  setCollectionView,
}) => {

  // WoW-style rarity border effects
  const getRarityBorderEffect = (rarity) => {
    const effects = {
      Common: {
        border: 'border-2 border-gray-400',
        glow: 'shadow-md',
        animation: ''
      },
      Rare: {
        border: 'border-2 border-blue-500',
        glow: 'shadow-lg shadow-blue-500/30',
        animation: 'hover:shadow-blue-500/50'
      },
      Epic: {
        border: 'border-3 border-purple-600',
        glow: 'shadow-xl shadow-purple-600/40',
        animation: 'hover:shadow-purple-600/60 animate-pulse'
      },
      Legendary: {
        border: 'border-4 border-orange-500',
        glow: 'shadow-2xl shadow-orange-500/50',
        animation: 'hover:shadow-orange-500/70 animate-pulse'
      }
    };
    return effects[rarity] || effects.Common;
  };

  const pokedexEntries = useMemo(() => {
    return imageDatabase.map((templateImage) => {
      const collectedImage = userCollection.find(item => {
        const itemId = item.id || item.imageID;
        const templateId = templateImage.id;
        return String(itemId).split('_')[0] === String(templateId) ||
               String(itemId) === String(templateId) ||
               item.name === templateImage.name ||
               item.imageName === templateImage.name;
      });
      return {
        ...templateImage,
        isCollected: !!collectedImage,
        dateObtained: collectedImage?.dateObtained || null,
        collectedData: collectedImage,
      };
    });
  }, [userCollection, imageDatabase]);

  const collectedCount = useMemo(() => pokedexEntries.filter(entry => entry.isCollected).length, [pokedexEntries]);

  const ObtainedView = () => (
    <>
      {userCollection.length === 0 ? (
        <div className="text-center py-12">
          <Gift className="mx-auto h-16 w-16 text-gray-400 mb-4" />
          <h3 className="text-xl font-bold text-gray-600 mb-2">No images collected yet</h3>
          <p className="text-gray-500">Draw your first daily image to start building your collection!</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {userCollection.map((image, index) => {
            const rarityEffect = getRarityBorderEffect(image.rarity);
            return (
              <div
                key={index}
                className={`bg-gray-50 rounded-lg overflow-hidden transition-all cursor-pointer relative group ${rarityEffect.border} ${rarityEffect.glow} ${rarityEffect.animation}`}
                onClick={() => setSelectedImage(image)}
              >
                <div className="relative">
                  <div className="relative w-full h-48 bg-gray-200 rounded-t-lg overflow-hidden">
                    <img src={image.url || image.imageUrl} alt="" className="absolute inset-0 w-full h-full object-cover filter blur-sm scale-110" />
                    <img
                      src={image.url || image.imageUrl}
                      alt={image.name || image.imageName}
                      className="relative w-full h-full object-contain z-10"
                      onError={(e) => { e.target.src = 'https://picsum.photos/400/300?text=Not+Found'; }}
                    />
                  </div>
                  <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-20 transition-all flex items-center justify-center">
                    <span className="text-white opacity-0 group-hover:opacity-100 transition-opacity text-sm font-medium bg-black bg-opacity-50 px-3 py-1 rounded">Click to view</span>
                  </div>
                  {/* Rarity corner indicator */}
                  {(image.rarity === 'Epic' || image.rarity === 'Legendary') && (
                    <div className="absolute top-2 left-2">
                      <div className={`w-3 h-3 rounded-full ${image.rarity === 'Epic' ? 'bg-purple-600' : 'bg-orange-500'} animate-pulse`}></div>
                    </div>
                  )}
                </div>
                <div className="p-4">
                  <div className="flex items-center justify-between mb-2">
                    <h3 className="font-bold text-gray-800">{image.name || image.imageName}</h3>
                    <span className={`px-2 py-1 rounded-full text-xs font-medium border ${getRarityColor(image.rarity)}`}>{image.rarity}</span>
                  </div>
                  <p className="text-gray-600 text-sm mb-2">{image.description}</p>
                  <div className="flex justify-between items-center">
                    <span className="text-xs text-purple-600 font-medium">{image.category}</span>
                    <span className="text-xs text-gray-500">{new Date(image.dateObtained).toLocaleDateString()}</span>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </>
  );

  const PokedexView = () => (
    <>
      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4">
        {pokedexEntries.map((entry, index) => {
          const rarityEffect = entry.isCollected ? getRarityBorderEffect(entry.rarity) : { border: 'border border-gray-300', glow: 'shadow-sm', animation: '' };
          return (
            <div
              key={entry.id}
              className={`rounded-lg overflow-hidden transition-all ${
                entry.isCollected 
                  ? `bg-white cursor-pointer transform hover:scale-105 ${rarityEffect.border} ${rarityEffect.glow} ${rarityEffect.animation}` 
                  : 'bg-gray-100 opacity-75 border border-gray-300'
              }`}
              onClick={() => entry.isCollected && setSelectedImage(entry.collectedData)}
            >
              <div className="relative">
                {entry.isCollected ? (
                  <img src={entry.url} alt={entry.name} className="w-full h-32 object-cover" onError={(e) => { e.target.src = 'https://via.placeholder.com/400x300?text=Image+Not+Found'; }} />
                ) : (
                  <div className="w-full h-32 bg-gradient-to-br from-gray-200 to-gray-300 flex items-center justify-center">
                    <div className="text-center"><div className="text-4xl text-gray-400 mb-1">?</div><div className="text-xs text-gray-500">Not Found</div></div>
                  </div>
                )}
                <div className="absolute top-2 left-2 bg-black bg-opacity-50 text-white text-xs px-2 py-1 rounded">#{String(index + 1).padStart(3, '0')}</div>
                {entry.isCollected && <div className="absolute top-2 right-2"><span className={`px-2 py-1 rounded-full text-xs font-medium ${getRarityColor(entry.rarity)}`}>{entry.rarity}</span></div>}
                {entry.isCollected && <div className="absolute bottom-2 right-2 bg-green-500 text-white rounded-full p-1"><svg className="h-3 w-3" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" /></svg></div>}
                {/* Epic/Legendary rarity indicator */}
                {entry.isCollected && (entry.rarity === 'Epic' || entry.rarity === 'Legendary') && (
                  <div className="absolute top-2 left-8">
                    <div className={`w-2 h-2 rounded-full ${entry.rarity === 'Epic' ? 'bg-purple-600' : 'bg-orange-500'} animate-pulse`}></div>
                  </div>
                )}
              </div>
              <div className="p-3">
                <h3 className={`font-bold text-sm ${entry.isCollected ? 'text-gray-800' : 'text-gray-400'}`}>{entry.isCollected ? entry.name : '???'}</h3>
                <p className={`text-xs mt-1 ${entry.isCollected ? 'text-gray-600' : 'text-gray-400'}`}>{entry.isCollected ? entry.category : 'Unknown'}</p>
                {entry.isCollected && entry.dateObtained && <p className="text-xs text-gray-500 mt-1">{new Date(entry.dateObtained).toLocaleDateString()}</p>}
              </div>
            </div>
          );
        })}
      </div>
      {collectedCount === imageDatabase.length && (
        <div className="mt-8 text-center p-6 bg-gradient-to-r from-yellow-100 to-orange-100 rounded-lg">
          <Star className="mx-auto h-12 w-12 text-yellow-500 mb-4" />
          <h3 className="text-xl font-bold text-gray-800 mb-2">🎉 Pokédex Complete! 🎉</h3>
          <p className="text-gray-600">Congratulations! You've collected all {imageDatabase.length} images!</p>
        </div>
      )}
    </>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 to-blue-600 p-2 sm:p-4">
      <div className="max-w-6xl mx-auto">
        <div className="bg-white rounded-lg shadow-xl overflow-hidden">
          {/* Enhanced Mobile-Friendly Header */}
          <div className="bg-gradient-to-r from-purple-600 to-blue-600 text-white p-4 sm:p-6">
            <div className="flex flex-col space-y-4">
              {/* Title and Progress */}
              <div className="text-center sm:text-left">
                <h2 className="text-xl sm:text-2xl font-bold mb-1">
                  {collectionView === 'obtained' ? '🎨 Your Collection' : '📚 Image Pokédex'}
                </h2>
                <div className="flex items-center justify-center sm:justify-start space-x-2">
                  <div className="bg-white bg-opacity-20 rounded-full px-3 py-1">
                    <span className="text-sm font-medium">
                      {collectedCount}/{imageDatabase.length} Collected
                    </span>
                  </div>
                  <div className="bg-white bg-opacity-20 rounded-full px-3 py-1">
                    <span className="text-sm font-medium">
                      {Math.round((collectedCount / imageDatabase.length) * 100)}%
                    </span>
                  </div>
                </div>
              </div>
              
              {/* Mobile-Optimized Navigation */}
              <div className="flex flex-col sm:flex-row justify-between items-center space-y-3 sm:space-y-0 sm:space-x-4">
                {/* View Toggle Buttons - Always Visible */}
                <div className="flex bg-white bg-opacity-20 rounded-xl p-1 w-full sm:w-auto">
                  <button
                    onClick={() => setCollectionView('obtained')}
                    className={`flex items-center justify-center space-x-2 px-4 py-2 rounded-lg text-sm font-medium transition-all flex-1 sm:flex-none ${
                      collectionView === 'obtained'
                        ? 'bg-white text-purple-600 shadow-lg transform scale-105'
                        : 'text-white hover:bg-white hover:bg-opacity-10'
                    }`}
                  >
                    <BookOpen className="h-4 w-4" />
                    <span className="hidden xs:inline">My</span>
                    <span>Collection</span>
                  </button>
                  <button
                    onClick={() => setCollectionView('pokedex')}
                    className={`flex items-center justify-center space-x-2 px-4 py-2 rounded-lg text-sm font-medium transition-all flex-1 sm:flex-none ${
                      collectionView === 'pokedex'
                        ? 'bg-white text-purple-600 shadow-lg transform scale-105'
                        : 'text-white hover:bg-white hover:bg-opacity-10'
                    }`}
                  >
                    <Grid3X3 className="h-4 w-4" />
                    <span>Pokédex</span>
                  </button>
                </div>
                
                {/* Home Button - Always Visible */}
                <button
                  onClick={() => setCurrentView('home')}
                  className="flex items-center justify-center space-x-2 bg-white bg-opacity-20 hover:bg-opacity-30 px-4 py-2 rounded-lg text-sm font-medium transition-all w-full sm:w-auto"
                >
                  <Home className="h-4 w-4" />
                  <span>Back Home</span>
                </button>
              </div>
            </div>
          </div>
          
          {/* Content */}
          <div className="p-4 sm:p-6">
            {collectionView === 'obtained' ? <ObtainedView /> : <PokedexView />}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CollectionScreen;
