import React, { useState, useEffect } from 'react';
import { User, Calendar, Star, Gift, LogOut, Home, Grid, TrendingUp } from 'lucide-react';
import { Amplify } from 'aws-amplify';
import { withAuthenticator } from '@aws-amplify/ui-react';
import '@aws-amplify/ui-react/styles.css';
import { generateClient } from 'aws-amplify/api';
import { getCurrentUser, signOut } from 'aws-amplify/auth';
import awsExports from './aws-exports';

// Configure Amplify
Amplify.configure(awsExports);
const client = generateClient();

// GraphQL mutations and queries
const createUser = /* GraphQL */ `
  mutation CreateUser($input: CreateUserInput!) {
    createUser(input: $input) {
      id
      username
      email
      totalImages
      lastClaimDate
      streakCount
      createdAt
      updatedAt
    }
  }
`;

const getUser = /* GraphQL */ `
  query GetUser($id: ID!) {
    getUser(id: $id) {
      id
      username
      email
      totalImages
      lastClaimDate
      streakCount
      collection {
        items {
          id
          imageID
          imageName
          rarity
          category
          description
          imageUrl
          dateObtained
        }
      }
    }
  }
`;

const createCollectionItem = /* GraphQL */ `
  mutation CreateCollectionItem($input: CreateCollectionItemInput!) {
    createCollectionItem(input: $input) {
      id
      userID
      imageID
      imageName
      rarity
      category
      description
      imageUrl
      dateObtained
    }
  }
`;

const updateUser = /* GraphQL */ `
  mutation UpdateUser($input: UpdateUserInput!) {
    updateUser(input: $input) {
      id
      username
      email
      totalImages
      lastClaimDate
      streakCount
    }
  }
`;

const listImageTemplates = /* GraphQL */ `
  query ListImageTemplates {
    listImageTemplates(filter: { isActive: { eq: true } }) {
      items {
        id
        name
        rarity
        category
        description
        imageUrl
      }
    }
  }
`;

const DailyImageCollector = ({ signOut, user }) => {
  const [currentView, setCurrentView] = useState('home');
  const [userCollection, setUserCollection] = useState([]);
  const [todaysClaim, setTodaysClaim] = useState(false);
  const [showNewImage, setShowNewImage] = useState(false);
  const [todaysImage, setTodaysImage] = useState(null);
  const [userData, setUserData] = useState(null);
  const [imageTemplates, setImageTemplates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [claiming, setClaiming] = useState(false);

  // Sample image database (fallback if no templates in database)
  const fallbackImages = [
    { id: '1', name: 'Mystic Forest', rarity: 'Common', imageUrl: 'https://images.unsplash.com/photo-1441974231531-c6227db76b6e?w=400&h=300&fit=crop', description: 'A peaceful forest shrouded in morning mist', category: 'Nature' },
    { id: '2', name: 'Desert Sunset', rarity: 'Rare', imageUrl: 'https://images.unsplash.com/photo-1509316975850-ff9c5deb0cd9?w=400&h=300&fit=crop', description: 'Golden dunes under a crimson sky', category: 'Landscape' },
    { id: '3', name: 'Crystal Lake', rarity: 'Epic', imageUrl: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=400&h=300&fit=crop', description: 'A serene lake reflecting the mountains', category: 'Nature' },
    { id: '4', name: 'Aurora Borealis', rarity: 'Legendary', imageUrl: 'https://images.unsplash.com/photo-1531366936337-7c912a4589a7?w=400&h=300&fit=crop', description: 'Dancing lights in the northern sky', category: 'Celestial' },
    { id: '5', name: 'Ancient Ruins', rarity: 'Common', imageUrl: 'https://images.unsplash.com/photo-1539650116574-75c0c6d9b86b?w=400&h=300&fit=crop', description: 'Weathered stones telling ancient stories', category: 'Historical' },
    { id: '6', name: 'Cosmic Nebula', rarity: 'Epic', imageUrl: 'https://images.unsplash.com/photo-1502134249126-9f3755a50d78?w=400&h=300&fit=crop', description: 'Swirling colors in deep space', category: 'Celestial' },
    { id: '7', name: 'Cherry Blossom', rarity: 'Rare', imageUrl: 'https://images.unsplash.com/photo-1522383225653-ed111181a951?w=400&h=300&fit=crop', description: 'Pink petals dancing in spring breeze', category: 'Nature' },
    { id: '8', name: 'Dragon\'s Peak', rarity: 'Legendary', imageUrl: 'https://images.unsplash.com/photo-1464822759844-d150baec53c1?w=400&h=300&fit=crop', description: 'A mountain peak touching the clouds', category: 'Landscape' }
  ];

  useEffect(() => {
    initializeUser();
  }, []);

  const initializeUser = async () => {
    try {
      setLoading(true);
      const currentUser = await getCurrentUser();

      // Try to get user data from database
      const userData = await client.graphql({
        query: getUser,
        variables: { id: currentUser.userId }
      });

      if (userData.data.getUser) {
        setUserData(userData.data.getUser);
        setUserCollection(userData.data.getUser.collection?.items || []);
        checkTodaysClaim(userData.data.getUser.lastClaimDate);
      } else {
        // Create new user in database
        const newUser = await client.graphql({
          query: createUser,
          variables: {
            input: {
              id: currentUser.userId,
              username: currentUser.username,
              email: currentUser.signInDetails?.loginId || '',
              totalImages: 0,
              streakCount: 0
            }
          }
        });
        setUserData(newUser.data.createUser);
        setUserCollection([]);
        setTodaysClaim(false);
      }

      // Load image templates
      const templates = await client.graphql({
        query: listImageTemplates
      });

      setImageTemplates(templates.data.listImageTemplates?.items || fallbackImages);

    } catch (error) {
      console.error('Error initializing user:', error);
      // Use fallback data
      setImageTemplates(fallbackImages);
    } finally {
      setLoading(false);
    }
  };

  const checkTodaysClaim = (lastClaimDate) => {
    const today = new Date().toDateString();
    setTodaysClaim(lastClaimDate === today);
  };

  const getRarityColor = (rarity) => {
    switch (rarity) {
      case 'Common': return 'text-gray-600 bg-gray-100';
      case 'Rare': return 'text-blue-600 bg-blue-100';
      case 'Epic': return 'text-purple-600 bg-purple-100';
      case 'Legendary': return 'text-yellow-600 bg-yellow-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const drawDailyImage = async () => {
    if (todaysClaim || claiming) return;

    setClaiming(true);
    try {
      // Weighted random selection based on rarity
      const weights = { Common: 50, Rare: 30, Epic: 15, Legendary: 5 };
      const totalWeight = Object.values(weights).reduce((a, b) => a + b, 0);
      const random = Math.random() * totalWeight;

      let currentWeight = 0;
      let selectedRarity = 'Common';

      for (const [rarity, weight] of Object.entries(weights)) {
        currentWeight += weight;
        if (random <= currentWeight) {
          selectedRarity = rarity;
          break;
        }
      }

      const availableImages = imageTemplates.filter(img => img.rarity === selectedRarity);
      const selectedImage = availableImages[Math.floor(Math.random() * availableImages.length)];

      if (!selectedImage) {
        throw new Error('No images available for selected rarity');
      }

      // Add to collection in database
      const newCollectionItem = await client.graphql({
        query: createCollectionItem,
        variables: {
          input: {
            userID: userData.id,
            imageID: selectedImage.id,
            imageName: selectedImage.name,
            rarity: selectedImage.rarity,
            category: selectedImage.category,
            description: selectedImage.description,
            imageUrl: selectedImage.imageUrl,
            dateObtained: new Date().toISOString()
          }
        }
      });

      // Update user stats
      const today = new Date().toDateString();
      const newStreakCount = userData.lastClaimDate === new Date(Date.now() - 86400000).toDateString() 
        ? userData.streakCount + 1 
        : 1;

      await client.graphql({
        query: updateUser,
        variables: {
          input: {
            id: userData.id,
            totalImages: userData.totalImages + 1,
            lastClaimDate: today,
            streakCount: newStreakCount
          }
        }
      });

      // Update local state
      const newItem = newCollectionItem.data.createCollectionItem;
      setUserCollection([...userCollection, newItem]);
      setUserData({
        ...userData,
        totalImages: userData.totalImages + 1,
        lastClaimDate: today,
        streakCount: newStreakCount
      });
      setTodaysClaim(true);
      setTodaysImage(selectedImage);
      setShowNewImage(true);

    } catch (error) {
      console.error('Error drawing daily image:', error);
      alert('Error claiming daily image. Please try again.');
    } finally {
      setClaiming(false);
    }
  };

  const handleSignOut = async () => {
    try {
      await signOut();
    } catch (error) {
      console.error('Error signing out:', error);
    }
  };

  const HomeScreen = () => (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 to-blue-600 p-4">
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow-xl p-6 mb-6">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center space-x-3">
              <User className="h-8 w-8 text-purple-600" />
              <div>
                <h2 className="text-xl font-bold text-gray-800">Welcome, {userData?.username}!</h2>
                <p className="text-gray-600">Collection: {userCollection.length} images</p>
                <p className="text-gray-600">Streak: {userData?.streakCount || 0} days</p>
              </div>
            </div>
            <button
              onClick={handleSignOut}
              className="flex items-center space-x-2 text-gray-600 hover:text-gray-800"
            >
              <LogOut className="h-5 w-5" />
              <span>Sign Out</span>
            </button>
          </div>

          <div className="grid md:grid-cols-2 gap-6">
            <div className="bg-gradient-to-r from-purple-100 to-blue-100 rounded-lg p-6">
              <div className="flex items-center space-x-3 mb-4">
                <Gift className="h-8 w-8 text-purple-600" />
                <h3 className="text-xl font-bold text-gray-800">Daily Draw</h3>
              </div>

              {!todaysClaim ? (
                <div>
                  <p className="text-gray-600 mb-4">Ready to discover today's image?</p>
                  <button
                    onClick={drawDailyImage}
                    disabled={claiming}
                    className="w-full bg-purple-600 hover:bg-purple-700 disabled:bg-purple-300 text-white font-medium py-3 px-4 rounded-lg transition-colors"
                  >
                    {claiming ? 'Drawing...' : 'Draw Today\'s Image'}
                  </button>
                </div>
              ) : (
                <div>
                  <p className="text-gray-600 mb-4">You've already claimed today's image!</p>
                  <div className="flex items-center space-x-2 text-green-600">
                    <Calendar className="h-5 w-5" />
                    <span>Come back tomorrow for a new image</span>
                  </div>
                </div>
              )}
            </div>

            <div className="bg-gradient-to-r from-blue-100 to-purple-100 rounded-lg p-6">
              <div className="flex items-center space-x-3 mb-4">
                <Grid className="h-8 w-8 text-blue-600" />
                <h3 className="text-xl font-bold text-gray-800">Your Collection</h3>
              </div>

              <div className="space-y-2">
                <div className="flex justify-between">
                  <span className="text-gray-600">Total Images:</span>
                  <span className="font-bold">{userCollection.length}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Daily Streak:</span>
                  <span className="font-bold">{userData?.streakCount || 0}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Rarest:</span>
                  <span className="font-bold">
                    {userCollection.length > 0 
                      ? userCollection.reduce((prev, curr) => {
                          const rarityOrder = { Common: 1, Rare: 2, Epic: 3, Legendary: 4 };
                          return rarityOrder[curr.rarity] > rarityOrder[prev.rarity] ? curr : prev;
                        }).rarity
                      : 'None'
                    }
                  </span>
                </div>
              </div>

              <button
                onClick={() => setCurrentView('collection')}
                className="w-full mt-4 bg-blue-600 hover:bg-blue-700 text-white font-medium py-3 px-4 rounded-lg transition-colors"
              >
                View Collection
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const CollectionScreen = () => (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 to-blue-600 p-4">
      <div className="max-w-6xl mx-auto">
        <div className="bg-white rounded-lg shadow-xl p-6">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-2xl font-bold text-gray-800">Your Collection</h2>
            <button
              onClick={() => setCurrentView('home')}
              className="flex items-center space-x-2 text-gray-600 hover:text-gray-800"
            >
              <Home className="h-5 w-5" />
              <span>Back to Home</span>
            </button>
          </div>

          {userCollection.length === 0 ? (
            <div className="text-center py-12">
              <Gift className="mx-auto h-16 w-16 text-gray-400 mb-4" />
              <h3 className="text-xl font-bold text-gray-600 mb-2">No images collected yet</h3>
              <p className="text-gray-500">Draw your first daily image to start building your collection!</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {userCollection.map((image, index) => (
                <div key={index} className="bg-gray-50 rounded-lg overflow-hidden shadow-md hover:shadow-lg transition-shadow">
                  <img
                    src={image.imageUrl}
                    alt={image.imageName}
                    className="w-full h-48 object-cover"
                  />
                  <div className="p-4">
                    <div className="flex items-center justify-between mb-2">
                      <h3 className="font-bold text-gray-800">{image.imageName}</h3>
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${getRarityColor(image.rarity)}`}>
                        {image.rarity}
                      </span>
                    </div>
                    <p className="text-gray-600 text-sm mb-2">{image.description}</p>
                    <div className="flex justify-between items-center">
                      <span className="text-xs text-purple-600 font-medium">{image.category}</span>
                      <span className="text-xs text-gray-500">
                        {new Date(image.dateObtained).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );

  // New image modal
  const NewImageModal = () => (
    showNewImage && todaysImage && (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
        <div className="bg-white rounded-lg shadow-xl max-w-md w-full p-6">
          <div className="text-center">
            <Star className="mx-auto h-12 w-12 text-yellow-500 mb-4" />
            <h3 className="text-xl font-bold text-gray-800 mb-2">New Image Discovered!</h3>
            <img
              src={todaysImage.imageUrl}
              alt={todaysImage.name}
              className="w-full h-48 object-cover rounded-lg mb-4"
            />
            <h4 className="font-bold text-lg text-gray-800">{todaysImage.name}</h4>
            <span className={`inline-block px-3 py-1 rounded-full text-sm font-medium ${getRarityColor(todaysImage.rarity)} mb-2`}>
              {todaysImage.rarity}
            </span>
            <p className="text-gray-600 text-sm mb-4">{todaysImage.description}</p>
            <button
              onClick={() => setShowNewImage(false)}
              className="w-full bg-purple-600 hover:bg-purple-700 text-white font-medium py-2 px-4 rounded-lg transition-colors"
            >
              Add to Collection
            </button>
          </div>
        </div>
      </div>
    )
  );

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-600 to-blue-600 flex items-center justify-center">
        <div className="text-white text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-white mb-4 mx-auto"></div>
          <p className="text-lg">Loading your collection...</p>
        </div>
      </div>
    );
  }

  return (
    <div>
      {currentView === 'home' && <HomeScreen />}
      {currentView === 'collection' && <CollectionScreen />}
      <NewImageModal />
    </div>
  );
};

export default withAuthenticator(DailyImageCollector);
